int Part1(char[] chars)
{
    for (var index = 0; index < chars.Length - 4; index++)
    {
        if (ContainsDistinct(chars[index..(index+4)], 4))
        {
            return index + 4;
        }
    }
    throw new InvalidOperationException("No marker found");
}

int Part2(char[] chars)
{
    for (var index = 0; index < chars.Length - 14; index++)
    {
        if (ContainsDistinct(chars[index..(index+14)], 14))
        {
            return index + 14;
        }
    }
    throw new InvalidOperationException("No message found");
}

bool ContainsDistinct(IReadOnlyCollection<char> chars, int count)
{
    if (chars.Count != count) throw new ArgumentException($"input must be {count} characters long");
    return chars.Distinct().Count() == count;
}